import React, { useRef, useEffect, useCallback } from 'react';
import { Container, Button } from './ModalElements';
import { useSpring, animated } from 'react-spring';
import { Background, ModalWrapper, ModalImg, ModalContent, CloseModalButton } from './ModalElements';
import { StyledModalButton, CateringFormInformation, CateringFormInformationTitle, CateringFormInformationSubtitle } from './ModalElements';

const Modal = ({ showModal, setShowModal }) => {
    const modalRef = useRef();

    const animation = useSpring({
      config: {
        duration: 250
      },
      opacity: showModal ? 0.9 : 0,
      transform: showModal ? `translateY(-150%)` : `translateX(100%)`
    });

    const closeModal = e => {
      if (modalRef.current === e.target) {
        setShowModal(false);
      }
    };

    const keyPress = useCallback(
      e => {
        if (e.key === 'Escape' && showModal) {
          setShowModal(false);
          console.log('I pressed');
        }
      },
      [setShowModal, showModal]
    );

    useEffect(
      () => {
        document.addEventListener('keydown', keyPress);
        return () => document.removeEventListener('keydown', keyPress);
      },
      [keyPress]
    );

    return (
      <>
        {showModal ? (
          <Background onClick={closeModal} ref={modalRef}>
            <animated.div style={animation}>
              <ModalWrapper showModal={showModal}>
                <ModalContent>
                <CateringFormInformation>
                    <CateringFormInformationTitle>Service Fee:</CateringFormInformationTitle>
                        <CateringFormInformationSubtitle>15% from the total amount at the end of the event.</CateringFormInformationSubtitle>
                    <CateringFormInformationTitle>Service Fee Includes:</CateringFormInformationTitle> 
                        <CateringFormInformationSubtitle>
                            Cost of Driving to the location of your event.
                            2 hours or serving food provided at the event.
                            Credit Card transaction fees.
                        </CateringFormInformationSubtitle>
                    <CateringFormInformationTitle>
                    Drive to you:
                    </CateringFormInformationTitle>
                        <CateringFormInformationSubtitle>Currently this only applies to Salt Lake and Utah County, as we expand our services maavailable elsewhere. Feel free to follow  on our social media's to get the latest updatewhat we're up to!</CateringFormInformationSubtitle>
                    <CateringFormInformationTitle>Additional hours:</CateringFormInformationTitle>
                        <CateringFormInformationSubtitle>If you will want or need any additional hours, the cost will be $100/hr.</CateringFormInformationSubtitle>
                    <CateringFormInformationTitle>Cancellation Policy:</CateringFormInformationTitle>
                        <CateringFormInformationSubtitle>If you must cancel your catering reservation instead of rescheduling,   will issues a 50% reup    until 14 days before the plan date. If you must cancel before that time, we can ieither a 25%  ref or a 50% refund in Juan Of A Kind Gift Cards.</CateringFormInformationSubtitle>
                </CateringFormInformation>
                </ModalContent>
                <CloseModalButton
                  aria-label='Close modal'
                  onClick={() => setShowModal(prev => !prev)}
                />
              </ModalWrapper>
            </animated.div>
          </Background>
        ) : null}
      </>
    );
  };

  export default Modal
